// 获取代币余额
func getTokenBalance(client *ethclient.Client, contractAddress common.Address, holderAddress common.Address, blockNumber *big.Int, ABI string) (*big.Int, error) {
	parsedABI, err := abi.JSON(strings.NewReader(ABI))
	if err != nil {
		return nil, err
	}

	callData, err := parsedABI.Pack("balanceOf", holderAddress)
	if err != nil {
		return nil, err
	}

	msg := ethereum.CallMsg{
		To:   &contractAddress,
		Data: callData,
	}

	result, err := client.CallContract(context.Background(), msg, blockNumber)
	if err != nil {
		return nil, err
	}

	balance := new(big.Int)
	balance.SetBytes(result)
	return balance, nil
}

// 获取所有持有者的代币余:
func getHoldersBalances(client *ethclient.Client, blockNumber *big.Int, holderAddresses []common.Address) map[common.Address]map[string]*big.Int {
	holders := make(map[common.Address]map[string]*big.Int)

	for _, address := range holderAddresses {
		arBalance, err := getTokenBalance(client, ARContractAddress, address, blockNumber, arTokenABI)
		if err != nil {
			fmt.Println(err)
		}
		aistrBalance, err := getTokenBalance(client, AISTRContractAddress, address, blockNumber, aiTokenABI)
		if err != nil {
			fmt.Println(err)
		}
		alchBalance, err := getTokenBalance(client, ALCHContractAddress, address, blockNumber, alTokenABI)
		if err != nil {
			fmt.Println(err)
		}

		totalBalance := new(big.Int).Add(arBalance, aistrBalance)
		totalBalance.Add(totalBalance, alchBalance)

		if totalBalance.Cmp(big.NewInt(0)) > 0 {
			holders[address] = map[string]*big.Int{
				"AR":    arBalance,
				"AISTR": aistrBalance,
				"ALCH":  alchBalance,
			}
		}
	}

	return holders
}

// 加权随机选择
func weightedRandomSelection(holders map[common.Address]map[string]*big.Int, numWinners int) []common.Address {
	var winners []common.Address
	var totalWeight *big.Int = big.NewInt(0)
	weights := make(map[common.Address]*big.Int)

	// 计算总权重
	for address, balances := range holders {
		weight := new(big.Int).Add(balances["AR"], balances["AISTR"])
		weight.Add(weight, balances["ALCH"])
		weights[address] = weight
		totalWeight.Add(totalWeight, weight)
	}

	rand.Seed(time.Now().UnixNano())
	selected := make(map[common.Address]bool)

	for len(winners) < numWinners && len(selected) < len(holders) {
		r := new(big.Int).Rand(rand.New(rand.NewSource(time.Now().UnixNano())), totalWeight)
		var cumulativeWeight *big.Int = big.NewInt(0)

		for address, weight := range weights {
			if selected[address] {
				continue
			}
			cumulativeWeight.Add(cumulativeWeight, weight)
			if r.Cmp(cumulativeWeight) < 0 {
				winners = append(winners, address)
				selected[address] = true
				totalWeight.Sub(totalWeight, weight)
				break
			}
		}
	}

	return winners
}

func main() {
	//	client, err := ethclient.Dial("https://mainnet.infura.io/v3/YOUR_INFURA_PROJECT_ID")
	client, err := ethclient.Dial("https://base-mainnet.infura.io/v3/d73727b62c7242369e00d00fabd847e5")
	if err != nil {
		log.Fatalf("Failed to connect to the Ethereum client: %v", err)
	}

	// 获取最新的区块编号
	blockNumberSrc, err := client.BlockNumber(context.Background())
	if err != nil {
		log.Fatalf("Failed to get block number: %v", err)
	}

	blockNumber := big.NewInt(int64(blockNumberSrc)) // 使用特定的区块编号作为快照
	holderAddresses := []common.Address{
		common.HexToAddress("0xa3c0b3e2504235ff20bdb4770fc74c06d04c6a26"),
		common.HexToAddress("0x8f86d51142cb45e49498d144abb2304922dc39af"),
	}

	holders := getHoldersBalances(client, blockNumber, holderAddresses)
	winners := weightedRandomSelection(holders, 1)

	for _, winner := range winners {
		fmt.Printf("Address: %s, Balances: AR=%s, AISTR=%s, ALCH=%s\n",
			winner.Hex(),
			holders[winner]["AR"].String(),
			holders[winner]["AISTR"].String(),
			holders[winner]["ALCH"].String())
	}
}
